import {ApiOauth} from "./APIOps";




async function registerDID(didDocument,signature){
    const response=ApiOauth();
    const url='https://api.entity.hypersign.id/api/v1/did/register';
    const headers={
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '+response.token,

    }

    const body={
        didDocument:didDocument,
        signInfo:[{
            signature:signature,
            verification_method_id:didDocument.id+'#key-1',
        }]
    }

    const resp=await fetch(url,{
        method:'POST',
        headers:headers,
        body:JSON.stringify(body),
    })

    const txnResp=await resp.json();
    return txnResp;
}

export {registerDID}
