import {ApiOauth} from "./APIOps";

import { API_URL } from "./constants";



async function registerDID(didDocument,signature){
    const response=await ApiOauth();
    const url=API_URL+'api/v1/did/register';
    const headers={
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '+response.access_token,

    }

    const body={
        didDocument:didDocument,
        signInfos:[{
            signature:signature,
            verification_method_id:didDocument.id+'#key-1',
        }]
    }

    const resp=await fetch(url,{
        method:'POST',
        headers:headers,
        body:JSON.stringify(body),
    })

    const txnResp=await resp.json();
    return txnResp;
}

export {registerDID}
