    async generateDID() {
      try {
        const mnemonic = generateMnemonic();
        const offlineSigner = await createWallet(mnemonic, 0);
        const hsDID = new HypersignDID({
          offlineSigner,
          namespace: this.namespace,
          nodeRestEndpoint: this.api,
          nodeRpcEndpoint: this.rpc
        });
        await hsDID.init();

        // seed modidied by hdpath
        const seed = await generateMnemonicToHDSeed(mnemonic, 0);
        const keys = await hsDID.generateKeys({
          seed
        });
        const didDocument = await hsDID.generate({
          publicKeyMultibase: keys.publicKeyMultibase
        });
        didDocument.service.push({
          id: didDocument.id + '#LinkedDomains',
          type: 'LinkedDomains',
          serviceEndpoint: 'https://' + this.domain + '/.well-known/did.json'
        });
        didDocument['@context'].push('https://w3id.org/security/suites/ed25519-2020/v1');
        localStorage.setItem('didDocument', JSON.stringify(didDocument));
        localStorage.setItem('mnemonic', mnemonic);
        localStorage.setItem('keys', JSON.stringify(keys));
      } catch (error) {
        console.log(error);
      }
    },
    async signcredential() {
      const hsCred = new HypersignVerifiableCredential({
        namespace: this.namespace,
        nodeRestEndpoint: this.api,
        nodeRpcEndpoint: this.rpc
      });
      const did_configuration = await hsCred.generate({
        subjectDid: JSON.parse(localStorage.getItem('didDocument')).id,
        type: ['DomainLinkageCredential'],
        schemaContext: ['https://identity.foundation/.well-known/did-configuration/v1'],
        issuerDid: JSON.parse(localStorage.getItem('didDocument')).id,
        expirationDate: new Date('2030-01-01T00:00:02.022Z').toISOString(),
        fields: {
          origin: this.domain
        }
      });
      const suite = new Ed25519Signature2020({
        key: new Ed25519VerificationKey2020({
          id: JSON.parse(localStorage.getItem('didDocument')).verificationMethod[0].id,
          controller: JSON.parse(localStorage.getItem('didDocument')).id,
          publicKeyMultibase: JSON.parse(localStorage.getItem('keys')).publicKeyMultibase,
          privateKeyMultibase: JSON.parse(localStorage.getItem('keys')).privateKeyMultibase
        })
      });
      const didDocument = JSON.parse(localStorage.getItem('didDocument'));
      delete did_configuration.credentialStatus;
      const signedCredential = await issue({
        credential: did_configuration,
        suite: suite,
        purpose: new purposes.AssertionProofPurpose({
          controller: {
            '@context': 'https://w3id.org/security/v2',
            id: didDocument.id,
            assertionMethod: [didDocument.verificationMethod[0].id]
          }
        }),
        documentLoader: async url => {
          return await jsonld.documentLoader(url);
        }
      });
      const didConfiguration = {
        '@context': 'https://identity.foundation/.well-known/did-configuration/v1',
        'linked_dids': [signedCredential]
      };
      localStorage.setItem('didConfiguration', JSON.stringify(didConfiguration));
    }
